// VulkanRenderable.h#ifndef __VULKANRENDERABLE__#define __VULKANRENDERABLE__class C3DPoint;class VulkanRenderable{public:	VulkanRenderable();        virtual ~VulkanRenderable();	virtual void Render(bool lowRes);		virtual long GetGeomID();		virtual bool IsOpaque();		virtual void DebugDraw(bool lowRes);		virtual bool GetCenter(C3DPoint &center, long &weight);            virtual void SetMaterialProperties(float specularity, float ambience, float diffuseness, float shininess);    virtual void GetMaterialProperties(float& specularity, float& ambience, float& diffuseness, float& shininess);        static const float 	kDefaultSpecularity;        static const float 	kDefaultAmbience;        static const float 	kDefaultDiffuseness;		static const float	kDefaultShininess;};#endif