package com.brazedblue.intervox.data;import java.util.*;public class DataManager  {  	Vector fData = new Vector();	    public DataManager() 	{    }		public void RemoveData(PatientData data)	{		fData.removeElement(data);	}		public void AddData(PatientData data)	{		fData.addElement(data);	}		public PatientData[] GetDataList()	{		PatientData[] result = new PatientData[fData.size()];				fData.copyInto(result);		return result;	}  }