package com.brazedblue.intervox.device;import com.brazedblue.intervox.image.SingleSpaceID;import com.brazedblue.intervox.tracker.TrackerSettingsAccess;public class DeviceOutput{	static public final int NO_TYPE = 0;	static public final int ORIG_COORDINATE = NO_TYPE + 1;	static public final int STRING = ORIG_COORDINATE + 1;	static public final int COMMAND = STRING + 1;	static public final int STATE = COMMAND + 1;	static public final int STATUS = STATE + 1;	static public final int FRAME_THAT_CLOSED = STATUS + 1;	static public final int POINT_COORDINATE = FRAME_THAT_CLOSED + 1;	static public final int ROTATION_MATRIX = POINT_COORDINATE + 1;	static public final int ORIG_ROTATION_MATRIX = ROTATION_MATRIX + 1;		static public final int NUM_OF_STATIONS = 2;	static public final int	ALL_STATIONS = 0;	static public final int	STATION1 = 1;	static public final int	STATION2 = 2;			protected int 					fType = NO_TYPE;	protected Object				fData;	private int						fStation = ALL_STATIONS;	private long 					fTime;	private TrackerSettingsAccess	fTrackerSettings;		public DeviceOutput()	{		fTime = System.currentTimeMillis();	}		public DeviceOutput(String string)	{		fTime = System.currentTimeMillis();		fType = STRING;		fData = string;			}		public DeviceOutput(Object data, int type)	{		fTime = System.currentTimeMillis();		fType = type;		fData = data;			}		public int GetDefaultType()	{		return fType;	}		public boolean IsFresh()	{		return (System.currentTimeMillis() - fTime) < 250;	}		public TrackerSettingsAccess GetTrackerSettings()	{		return fTrackerSettings;	}		public void SetTrackerSettings(TrackerSettingsAccess trackerSettings)	{		fTrackerSettings = trackerSettings;	}		public SingleSpaceID GetSpaceID()	{		return SingleSpaceID.GetRealSpaceID();	}		public Object GetDataOfType(int type)	{		if (type == fType)		{			return fData;		}		else		{			return null;		}		}		public void SetData(Object data, int type)	{		fData = data;		fType = type;	}		public long GetTime()	{		return fTime;	}		protected void SetStation(int station)	{		fStation = station;	}		public int GetStation()	{		return fStation;	}}