package com.brazedblue.intervox.device;public class DeviceStatus /* implements anInterface */ {  boolean fContinuous;  boolean fAtPoint;  boolean fDecimalUnits;  public DeviceStatus(boolean continuous, boolean atPoint, boolean decimalUnits) {    fContinuous = continuous;    fAtPoint = atPoint;    fDecimalUnits = decimalUnits;  }  public boolean IsContinuous() {    return fContinuous;  }  public boolean AtPoint() {    return fAtPoint;  }  public boolean DecimalUnits() {    return fDecimalUnits;  }}