package com.brazedblue.intervox.display;import javax.swing.JComponent;import java.util.Vector;import java.awt.Graphics;import java.awt.Dimension;import java.awt.Insets;public class AdornedComponent extends JComponent { 		private Vector 			fAdorners = new Vector();	private ComponentSizer	fSizer;	    public AdornedComponent() 	{		    }		public void SetComponentSizer(ComponentSizer sizer)	{		fSizer = sizer;	}		public void AddComponentAdorner(ComponentAdorner adorner)	{		fAdorners.addElement(adorner);	}		public Dimension getPreferredSize()	{		if (fSizer != null)		{			Dimension result = fSizer.GetPreferredSize();/*			Insets inset = getInsets();			result.width += inset.left + inset.right;			result.height += inset.top + inset.bottom; */						return result;		}		else		{			return super.getPreferredSize();		}	}		public void paintComponent(Graphics g) 	{		for (int i = 0; i < fAdorners.size(); i++)		{			ComponentAdorner adorner = (ComponentAdorner)fAdorners.elementAt(i);			adorner.Paint(g, this);		}    }		public void RemoveComponentAdorner(ComponentAdorner adorner)	{		fAdorners.removeElement(adorner);	}		public void ReplaceComponentAdorner(ComponentAdorner oldAdorner, ComponentAdorner newAdorner)	{		int index = fAdorners.indexOf(oldAdorner);				if (index >= 0)		{			fAdorners.setElementAt(newAdorner, index);		}	}}