package com.brazedblue.intervox.display;import java.awt.*;import java.awt.event.*;public class CloseActionListener implements ActionListener {  private Window fWindow;  private Object fCloseObject;  public CloseActionListener(Window window) {    fWindow = window;  }  public void actionPerformed(ActionEvent e) {    fCloseObject = e.getSource();    Toolkit.getDefaultToolkit()        .getSystemEventQueue()        .postEvent(new WindowEvent(fWindow, WindowEvent.WINDOW_CLOSING));  }  public Object GetCloseObject() {    return fCloseObject;  }}