package com.brazedblue.intervox.display;import javax.swing.*;import com.brazedblue.intervox.data.*;import com.brazedblue.intervox.tracker.TrackerController;public class ConfigurationEditPanel extends JTabbedPane implements DataModelListener {  	private ConfigEditModel				fConfigEditModel;	private OffsetCalculationDisplay 	fOffsetDisplay;	private LocalizerOptionsPane		fLocalizerOptionsPane;	private LayoutOptionsPane			fLayoutOptionsPane;		private static final String 		kOffsetDisplay = "Offset Calculation";    public ConfigurationEditPanel(ConfigEditModel configEditModel, TrackerController tracker) 	{		fConfigEditModel = configEditModel;		fConfigEditModel.AddListener(this);		Configuration selectedConfig = fConfigEditModel.GetSelectedConfiguration();				setBorder(BorderFactory.createEtchedBorder());				fOffsetDisplay = new OffsetCalculationDisplay(tracker, selectedConfig);		addTab(kOffsetDisplay, fOffsetDisplay);		setSelectedIndex(0);				fLocalizerOptionsPane = new LocalizerOptionsPane(tracker, selectedConfig);		addTab("Localizer", fLocalizerOptionsPane);				fLayoutOptionsPane = new LayoutOptionsPane(fConfigEditModel.GetLayoutSelections());		addTab("Layouts", fLayoutOptionsPane);    }		public void DataModelChanged(DataModel model, DataChangedEvent event)	{		if (event.GetEventNumber() == DataChangedEvent.SELECTED)		{			Configuration selectedConfig = fConfigEditModel.GetSelectedConfiguration();						fLocalizerOptionsPane.SetConfiguration(selectedConfig);			fOffsetDisplay.SetConfiguration(selectedConfig);			fLayoutOptionsPane.SetLayoutSelections(fConfigEditModel.GetLayoutSelections());			fLayoutOptionsPane.SetupControls();		}	}		void Dispose()	{		if (fLayoutOptionsPane != null)		{			fLayoutOptionsPane.Dispose();			fLayoutOptionsPane = null;		}	}  }