package com.brazedblue.intervox.display;import java.awt.MenuItem;import java.awt.Point;import java.util.EventObject;public class ContextPopupEvent extends EventObject {  MenuItem fMenuItem;  Point fPressedPoint;  public ContextPopupEvent(Object source, MenuItem menuItem, Point pressedPoint) {    super(source);    fMenuItem = menuItem;    fPressedPoint = pressedPoint;  }  public MenuItem GetMenuItem() {    return fMenuItem;  }  public Point GetPressedPoint() {    return fPressedPoint;  }}