package com.brazedblue.intervox.display;import java.awt.*;import java.lang.*;import javax.swing.JDialog;public class CustomOS  /* implements anInterface */ {     public CustomOS() 	{    }		static public Rectangle GetDesktopRect()	{               Dimension size = Toolkit.getDefaultToolkit().getScreenSize();                Rectangle result = new Rectangle(0, 0, size.width, size.height);				return result;	}		static public void CenterDialog(JDialog dlog)	{		Dimension prefSize = dlog.getPreferredSize();				Rectangle desktop = GetDesktopRect();				Point location = new Point(desktop.x, desktop.y);				if (desktop.height > prefSize.height)		{			location.y += (desktop.height - prefSize.height) / 3;		}		if (desktop.width > prefSize.width)		{			location.x += (desktop.width - prefSize.width) / 2;		}				dlog.setLocation(location);	} }