package com.brazedblue.intervox.display;import java.awt.*;public class DrawRubber3D extends Draw3D /* implements anInterface */ {  private Color darkerColor = Color.darkGray;  private Color darkestColor = Color.black;  public DrawRubber3D() {    fRaiseHt = 4;  }  void SetColor(int position, int state, Graphics g, int i) {    switch (state) {      case NORMAL:        {          switch (position) {            case TOP:            case LEFT:              switch (i) {                case 0:                  g.setColor(darkerColor);                  break;                case 1:                  g.setColor(flatColor);                  break;                case 2:                  g.setColor(lightedColor);                  break;              }              break;            case BOTTOM:            case RIGHT:              switch (i) {                case 0:                  g.setColor(darkestColor);                  break;                case 1:                  g.setColor(darkerColor);                  break;                case 2:                  g.setColor(shadowColor);                  break;              }              break;            case CENTER:              g.setColor(flatColor);              break;          }        }        break;      case PRESSED:        {          switch (position) {            case TOP:            case LEFT:              switch (i) {                case 0:                  g.setColor(darkerColor);                  break;                case 1:                  g.setColor(lightedColor);                  break;                case 2:                  g.setColor(shadowColor);                  break;                case 3:                  g.setColor(darkestColor);                  break;              }              break;            case BOTTOM:            case RIGHT:              switch (i) {                case 0:                  g.setColor(darkestColor);                  break;                case 1:                  g.setColor(shadowColor);                  break;                case 2:                  g.setColor(lightedColor);                  break;                case 3:                  g.setColor(darkerColor);                  break;              }              break;            case CENTER:              g.setColor(flatColor);              break;          }        }    }  }  protected int GetRaiseHeight(int state) {    if (state == PRESSED) {      return 4;    } else {      return 3;    }  }}