package com.brazedblue.intervox.display;import javax.swing.*;public class ImageSeriesViewControls extends JPanel /* implements anInterface */ {  ImageControlSettings fControlSettings;  ImageViewType fImageViewType;  public ImageSeriesViewControls() {    setLayout(new BoxLayout(this, BoxLayout.Y_AXIS));  }  public ImageControlSettings GetImageControlSettings() {    return fControlSettings;  }  /* obsolete  	public java.awt.Dimension getPreferredSize()  	{  		java.awt.Dimension result = super.getPreferredSize();  		if (neurosynch.util.Debug.fgDebugging && false)  		{  			neurosynch.util.Debug.PrintStackTrace(result.toString());  		}  		return result;  	}  */  public void SetImageControlSettings(ImageControlSettings settings) {    if (fControlSettings != null) {      fControlSettings.Dispose();      removeAll();    }    fControlSettings = settings;    if (fControlSettings != null) {      fControlSettings.SetUp(this);      if (fImageViewType != null) {        fControlSettings.SetImageViewType(fImageViewType);      }    }  }  public void SetLayout(LayoutModel layout) {    if (fControlSettings != null && layout != null) {      fControlSettings.SetLayout(layout);    }  }  public void SetImageViewType(ImageViewType imageViewType) {    fImageViewType = imageViewType;    if (fControlSettings != null && fImageViewType != null) {      fControlSettings.SetImageViewType(fImageViewType);    }  }  public void Dispose() {    if (fControlSettings != null) {      fControlSettings.Dispose();    }    fImageViewType = null;    fControlSettings = null;  }}