package com.brazedblue.intervox.file;import java.io.*;import java.util.Vector;public class FileSuffixFilter implements FilenameFilter /* implements anInterface */ {	Vector		fSuffixes;  	public FileSuffixFilter(String suffix) 	{            AddSuffix(suffix);	}                public FileSuffixFilter()        {        }                public void AddSuffix(String suffix)        {            if (fSuffixes == null)            {	                fSuffixes = new Vector();            }                        fSuffixes.add(suffix);        }        		public boolean accept(File dir, String name)	{		boolean result = false;		                                if (fSuffixes != null)                {                    String fileSuffix = GetSuffix(name);                    int size = fSuffixes.size();                    for (int i = 0; i < size; i++)                    {                            String suffix = (String)fSuffixes.elementAt(i);                            if (fileSuffix.equalsIgnoreCase(suffix))                            {                                result = true;                                break;                            }                    }                }                else                {                    result = true;                }				return result;	}                static public String GetSuffix(String fileName)        {            String result = null;            int lastIndex = fileName.lastIndexOf('.');                        if (lastIndex >= 0)            {                result = fileName.substring(lastIndex);                result.trim();            }            else            {                result = "";            }                    return result;        }}