package com.brazedblue.intervox.geometry;import javax.vecmath.Matrix4f;public class Matrix4 extends Matrix4f {  public Matrix4() {}  public void SetIdentity() {    setIdentity();  }  public void SetRotX(float xAngle) {    rotX(xAngle);  }  public void SetRotY(float yAngle) {    rotY(yAngle);  }  public float[] GetColumnMajorArray() {    float[] result = new float[16];    int index = 0;    for (int col = 0; col < 4; col++) {      for (int row = 0; row < 4; row++) {        result[index++] = getElement(row, col);      }    }    return result;  }  public void Multiply(Matrix4 mat1, Matrix4 mat2) {    mul(mat1, mat2);  }}