package com.brazedblue.intervox.geometry;public class ReferenceState implements java.io.Serializable {	Matrix3		fRotation;	FloatPoint	fLocation;	static final long			serialVersionUID = -5346270828705196520L;	    public ReferenceState(Matrix3 rotation, FloatPoint point) 	{		fRotation = new Matrix3(rotation);		fLocation = new FloatPoint(point);    }		public Matrix3 GetRotation()	{		return fRotation;	}		public FloatPoint GetLocation()	{		return fLocation;	}  }