package com.brazedblue.intervox.image;import com.brazedblue.intervox.geometry.Unit;import com.brazedblue.intervox.util.Debug;class FloatUnit implements Unit, java.io.Serializable {  	float fUnit;	static final long			serialVersionUID = -9153430080584031117L;    FloatUnit(float unit) 	{		fUnit = unit;				if (Debug.fgDebugging && unit == 0)		{			Debug.PrintStackTrace("unit == 0");		}    }		public float Convert(float value, float unit)	{		return value * (fUnit / unit);	}  }