package com.brazedblue.intervox.image;import com.brazedblue.intervox.data.*;import com.brazedblue.intervox.display.ProgressBroadcaster;import com.brazedblue.intervox.display.ProgressCancelledException;import com.brazedblue.intervox.display.SwingSafeProgress;import com.brazedblue.intervox.file.*;import com.brazedblue.intervox.util.*;import java.awt.*;import javax.swing.*;public class JPEGFileLoaderThread extends FileLoaderThread /* implements anInterface */ {  String fDirectory;  String[] fFileList;  ProgressBroadcaster fBroadcaster;  AWTImageReceiver fReceiver;  Component fComp;  public JPEGFileLoaderThread(      String directory,      String[] fileList,      ProgressBroadcaster broadcaster,      AWTImageReceiver receiver,      Component comp) {    fFileList = fileList;    fDirectory = directory;    fBroadcaster = new SwingSafeProgress(broadcaster);    fReceiver = receiver;    fComp = comp;  }  public void run() {    MediaTracker tracker = new MediaTracker(fComp);    try {      for (int i = 0; i < fFileList.length; i++) {        Image image = ImageHandler.GetLoadedJpegImage(fDirectory + fFileList[i], tracker);        if (image != null) {          fReceiver.AddImage(image);          fBroadcaster.UpdateCurrentValue(fFileList[i], i);        }        MyYield();      }      fReceiver.FinishReceiving(fBroadcaster);      fBroadcaster.Finish(true);    } catch (java.io.IOException e) {      fBroadcaster.Finish(false);      NeuroSynchUtil.ErrorMessage(          "Loading jpeg files, " + e.getMessage(), NeuroSynchUtil.kShowDlog);    } catch (ProgressCancelledException e2) {      fBroadcaster.Finish(false);    }  }}