package com.brazedblue.intervox.serial;import java.io.IOException;/** * Generic serial driver interface * * @version 1 6/4/98 * @author Steve Meyer *///	Right Now Mac OS 9 specificpublic interface SerialDriver {  public void Open() throws IOException;  public int GetAvailableBytes() throws IOException;  public void Close() throws IOException;  public void Write(byte[] byteArray) throws IOException;  public int Read(byte[] byteArray) throws IOException;  public boolean IsOpen();}