package com.brazedblue.intervox.serial;import java.io.*;		// this should be accessed with a PrintWriterclass SerialOutStream extends ByteArrayOutputStream{    /**     * SerialDriver object      */	SerialDriver fSerialDriver;		    /**     * Constructor.     * @param a SerialDriver object     */	public SerialOutStream(SerialDriver driver)	{		fSerialDriver = driver;	}		public void flush() throws IOException	//	override		{		byte[] byteArray = toByteArray();		reset();				fSerialDriver.Write(byteArray);	}		public void open() throws IOException		{		fSerialDriver.Open();	}			public void close() throws IOException	{		fSerialDriver.Close();	}		protected void finalize() throws Throwable		{		try		{			close();		}		catch (IOException except)		{			// do nothing		}	}}