package com.brazedblue.intervox.tracker;class PortMonitor extends Thread{	Tracker fTracker;        boolean fStopped = false;	public PortMonitor(Tracker itsTracker)	{		fTracker = itsTracker;	}		synchronized void SetNullTracker()	{		fTracker = null;	}		public void run() 	{		if (fTracker != null)		{			fTracker.run(this);		}			}                public void Stop()        {            fStopped = true;        }        public boolean Stopped()        {            return fStopped;        }}