package com.brazedblue.intervox.tracker;import com.brazedblue.intervox.device.*;class TrackerStateEvent extends DeviceOutput /* implements anInterface */ {  Boolean fState;  public TrackerStateEvent(String command, boolean state) {    super(command);    fState = new Boolean(state);  }  public int GetDefaultType() {    return COMMAND;  }  public Object GetDataOfType(int type) {    if (type == STATE) return fState;    else if (type == COMMAND) return fData;    else return null;  }}