package com.brazedblue.intervox.util;import java.io.*;class DualPrintStream extends PrintStream {  PrintStream fOtherStream;  public DualPrintStream(OutputStream out, boolean autoFlush, PrintStream otherStream) {    super(out, autoFlush);    fOtherStream = otherStream;  }  public void flush() {    super.flush();    fOtherStream.flush();  }  public void close() {    super.close();    fOtherStream.close();  }  public void write(int b) {    super.write(b);    fOtherStream.write(b);  }  public void write(byte buf[], int off, int len) {    super.write(buf, off, len);    fOtherStream.write(buf, off, len);  }}