package com.brazedblue.intervox.view3D;import java.awt.Color;public class Mesh3D {  Engine3D fEngine3D;  long fMeshRef;  Mesh3D(Engine3D engine, long meshRef) {    fEngine3D = engine;    fMeshRef = meshRef;  }  protected void finalize() {    Dispose();  }  long GetRef() {    return fMeshRef;  }  public void SetColor(Color color) {    fEngine3D.SetGeometryColor(color, fMeshRef);  }  public void SetTransparency(float transparency) {    fEngine3D.SetGeometryTransparency(transparency, fMeshRef);  }  public int GetResolution() {    return fEngine3D.GetGeometryResolution(fMeshRef);  }  public void Dispose() {    if (fMeshRef != 0) {      long ref = fMeshRef;      fMeshRef = 0;      fEngine3D.Dispose3DGeometry(ref);    }  }}