package com.brazedblue.intervox.view3D;import com.brazedblue.intervox.util.Debug;import com.brazedblue.intervox.util.NeuroSynchUtil;public class NativeError /* implements anInterface */ {  public String[] fErrorStrings = new String[3];  public NativeError() {}  /*	public void SetError(int errCode, String errString)  {  	fErrorString = errString;  	fError = errCode;  	if (fErrorString != null)  	{  		NeuroSynchUtil.ErrorMessage("NativeError.SetError error :" +  fErrorString, NeuroSynchUtil.kNoDlog);  	}  } */  public String[] GetErrStrings() {    return fErrorStrings;  }  /*	public void SetAlert(boolean alert)  {  	fAlert = alert;  } */  public void DebugMessage(String messageStr) {    if (Debug.fgDebugging) {      NeuroSynchUtil.ErrorMessage(messageStr, NeuroSynchUtil.kNoDlog);    }  }  public void DisplayIfError() {    if (fErrorStrings[0] != null) {      String message =          "Native error code: "              + fErrorStrings[0]              + (fErrorStrings[1] != null ? fErrorStrings[1] : "");      boolean showDlog =          fErrorStrings[2] != null ? NeuroSynchUtil.kShowDlog : NeuroSynchUtil.kNoDlog;      NeuroSynchUtil.ErrorMessage(message, showDlog);    }    for (int i = 0; i < 3; i++) {      fErrorStrings[i] = null;    }  }}